function checkPhone(context) {
	jQuery('.js-check-phone', context).each(function(){
		var button = jQuery(this),
			input = button.closest('.form-row').find(':input'),
			checkForm = jQuery('.js-check-phone-form'),
			resentLink = jQuery('.js-resent-code'),
			textSending = button.attr('data-text-sending'),
			textError = button.attr('data-text-error'),
			textVerified = button.attr('data-text-verified'),
			textDefault = button.text(),
			sendUrl = button.attr('data-send-url'),
			verifiedPhones = JSON.parse(button.attr('data-verified')),
			
			isPhoneVerified = function(phone){
				var clearPhone = String(phone).replace(/[^0-9]/g, '');
				
				return $.inArray(clearPhone, verifiedPhones) !== -1;
			};
		
		


		input.on('keyup change', function(){
			var phone = input.val();
			
			if (phone.length == 19 && !isPhoneVerified(phone)) {
				//button.removeClass('hidden');
				button.removeClass('disabled');
				button.removeClass('phone-verified');				
				button.text(textDefault);
			} else if (phone.length == 19) {
				//button.addClass('disabled');
				button.addClass('phone-verified');										

				button.text(textVerified);
				button.removeClass('disabled');
				//button.removeClass('hidden');
				button.removeClass('phone-not-verified');
			} else {				
				//button.addClass('hidden');
				button.addClass('disabled');
				button.removeClass('phone-not-verified');
			}
		}).trigger('keyup');
		
		button.on('click', function(e){
			e.preventDefault();
			
			var phone = input.val();

			if (isPhoneVerified(phone)) {
				return false;
			}
			
			button.text(textSending);
			
			jQuery.ajax({
				type: 'POST',
				url: sendUrl,
				data: 'phone='+phone,
				success: function(response, status, xhr) {
					checkForm
						.unbind('submit')
						.on('submit', function(e) {
							var form = jQuery(this),
								checkButton = form.find('button'),
								checkInput = form.find('input[type=text]'),
								textSending = checkButton.attr('data-text-sending'),
								textError = checkButton.attr('data-text-error'),
								textDefault = checkButton.text();


							e.preventDefault();
							
							if (form.valid() === true) {
								checkButton.text(textSending);

								form.find('.flash').remove();
								jQuery.ajax({
									type: 'POST',
									url: form.attr('action'),
									data: form.serialize(),
									success: function(response, status, xhr){
										checkButton.text(textDefault);
										if (response.success) {
											verifiedPhones.push(phone.replace(/[^\+0-9]/, ''));
											//button.addClass('disabled');
											button.addClass('phone-verified');											
											button.text(textVerified);
											button.removeClass('hidden');
											button.removeClass('phone-not-verified');
											form.closest('.lightbox').find('.lightbox-close').eq(0).trigger('click');
											input.removeClass('has-error');
										} else {
											checkInput.addClass('has-error');
											jQuery('<div class="flash flash-error">' + response.error + '</div>').insertAfter(checkInput);
										}
									},
									error: function(jqXHR, textStatus, errorThrown){
										checkButton.text(textError);
									}
								});
							}
						})
					;

					resentLink
						.unbind('click')
						.bind('click', function(e){
							var button = jQuery(this),
								form = button.closest('form'),
								textSending = button.attr('data-text-sending'),
								textError = button.attr('data-text-error'),
								textDefault = button.text();

							e.preventDefault();

							form.find('.flash').remove();
							form[0].reset();
							form.data('validator').resetForm();

							button.text(textSending);

							jQuery.ajax({
								type: 'POST',
								url: sendUrl,
								data: 'phone='+phone,
								success: function(){
									button.text(textDefault);
								},
								error: function(){
									button.text(textError);
								}
							});
						})
					;
					
					jQuery.fancybox.open(button, {
						parentEl: 'body',
						margin: [50, 0],
						smallBtn: false,
						closeClickOutside: true,
						touch: false,
						speed: 400
					});

					button.text(textDefault);
				},
				error: function(jqXHR, textStatus, errorThrown){
					button.text(textError);
				}
			});
		});
	});
	
}

