function initFormsValidation(context) {
	jQuery('.js-form-validate', context).each(function(){
		var form = jQuery(this);
		form.validate({
			ignore: '.passed, :hidden',
			validClass: "passed",
			errorClass: 'error',
			errorPlacement: function(error, element) {
				return false;
			},
			highlight: function(element, errorClass, validClass) {
				jQuery(element).addClass("has-error");
				jQuery(element).parents(".radio-list").addClass("list-has-error");
				jQuery(element).next("span.jcf-select").addClass("select-has-error");
			},
			unhighlight: function(element, errorClass, validClass) {
				jQuery(element).removeClass("has-error");
				jQuery(element).parents(".radio-list").removeClass("list-has-error");
				jQuery(element).next("span.jcf-select").removeClass("select-has-error");
			}
		});
		
		form.on('submit', function(e){
			if (form.valid() !== true) {
				e.preventDefault();
			}
		});
	});
	jQuery('.js-form-validate', context)
}

(function ($) {
    // Behind the scenes method deals with browser
    // idiosyncrasies and such
    $.caretTo = function (el, index) {
        if (el.createTextRange) { 
            var range = el.createTextRange(); 
            range.move("character", index); 
            range.select(); 
        } else if (el.selectionStart != null) { 
            el.focus(); 
            el.setSelectionRange(index, index); 
        }
    };

    // The following methods are queued under fx for more
    // flexibility when combining with $.fn.delay() and
    // jQuery effects.

    // Set caret to a particular index
    $.fn.caretTo = function (index, offset) {
        return this.queue(function (next) {
            if (isNaN(index)) {
                var i = $(this).val().indexOf(index);
                
                if (offset === true) {
                    i += index.length;
                } else if (offset) {
                    i += offset;
                }
                
                $.caretTo(this, i);
            } else {
                $.caretTo(this, index);
            }
            
            next();
        });
    };

}(jQuery));

