function initShowIfValue(context) {
	jQuery('[data-show-if-field]', context).each(function(){
		var self = jQuery(this),
			field = jQuery(self.attr('data-show-if-field'), context),
			showIfValue = String(self.attr('data-show-if-value')).split('|');
		
		if (showIfValue.indexOf(field.val()) !== -1) {
			self.removeClass('hidden');
		} else {
			self.addClass('hidden');
		}
		
		field.on('change', function(){
			if (showIfValue.indexOf(field.val()) !== -1) {
                self.removeClass('hidden');
			} else {
                self.addClass('hidden');
			}
		});
	});

	$('.disabled').click(function (e) {
        return false;
    })
}

function initInlineFormEditing(context)
{
	jQuery(".open-editing", context).click(function(e){
		e.preventDefault();
		var thisBtn = jQuery(this);
		var thisForm = thisBtn.closest("form");
		if (!thisForm.length) {
			return;
		}
		jQuery(thisForm).addClass("edit-active");
		var inputs = jQuery(thisForm).find("input:not('.button'), select");
		jQuery(inputs).each(function(){
			var eachInput = jQuery(this);
			if (eachInput.attr('disabled') && jQuery(thisForm).hasClass("edit-active")) {
				eachInput.removeAttr("disabled");
				if(eachInput.is("select")) {
					eachInput.next(".jcf-select").removeClass("jcf-disabled");
				}
			}

			if(!eachInput.attr("data-value")) {
				eachInput.attr("data-value", eachInput.val());
			}
		});
	});
	jQuery(".back-btn", context).click(function(e){
		e.preventDefault();
		var thisBtn = jQuery(this);
		var thisForm = thisBtn.closest(".data-info");
		jQuery(thisForm).removeClass("edit-active");
		var inputs = jQuery(thisForm).find("input:not('.button'), select");
		jQuery(inputs).each(function(){
			var eachInput = jQuery(this);
			eachInput.removeClass("has-error");
			eachInput.attr("disabled", "disabled");
			if(eachInput.is("select")) {
				eachInput.next(".jcf-select").addClass("jcf-disabled");
			}
			var rememberFocus;
			if(rememberFocus = eachInput.attr("data-value")) {
				eachInput.val(rememberFocus);
				eachInput.trigger('change');
			}
		});
	});
	jQuery(".submit-profile", context).click(function(e) {
		var thisBtn = jQuery(this);
		var formHolder = thisBtn.closest(".edit-active");
		var form = jQuery(formHolder);
		form.validate({
			ignore: ':hidden',
			validClass: "passed",
			errorClass: 'error',
			errorPlacement: function(error, element) {
				return false;
			},
			highlight: function(element, errorClass, validClass) {
				jQuery(element).addClass("has-error");
				jQuery(element).parents(".radio-list").addClass("list-has-error");
				jQuery(element).next("span.jcf-select").addClass("select-has-error");
			},
			unhighlight: function(element, errorClass, validClass) {
				jQuery(element).removeClass("has-error");
				jQuery(element).parents(".radio-list").removeClass("list-has-error");
				jQuery(element).next("span.jcf-select").removeClass("select-has-error");
			}
		});
		if (form.valid() !== true){
			e.preventDefault();
		}
	});
}

function registerValidationRules() {
	// Custom method to validate username
	jQuery.validator.addMethod("usernameRegex", function(value, element) {
		return this.optional(element) || /^[a-zA-Z0-9]*$/i.test(value);
	});
	jQuery.validator.addMethod("onlyLetter", function(value, element) {
		return this.optional(element) || /^[а-яА-ЯёЁіІїЇєЄґҐa-zA-Z \-, ]+$/i.test(value);
	});
    jQuery.validator.addMethod("notSpaces", function(value, element) {
        return this.optional(element) || value.trim().length;
    });
	jQuery.validator.addMethod("letterAndNumber", function(value, element) {
		// return this.optional(element) || i.test(value);
		return this.optional(element) || value.match(/^[а-яА-ЯёЁa-zA-Z]{2}[0-9]{6}$/);
	});
	jQuery.validator.addMethod("phoneValidate", function(value, element) {
		return this.optional(element) || /^\+38 \([0-9]{3}\) [0-9]{3}-[0-9]{2}-[0-9]{2}$/i.test(value);
	});
	jQuery.validator.addMethod("passportValidate", function(value, element) {
		return this.optional(element) || /^[а-яА-ЯёЁ]{2} [0-9]{6}$/i.test(value);
	});
	jQuery.validator.addMethod("documentNumberValidate", function(value, element) {
		return this.optional(element) || /^[0-9]{9}$/i.test(value);
	});
	jQuery.validator.addMethod("passportIssuedByNumberValidate", function(value, element) {
		return this.optional(element) || /^[0-9]{4}$/i.test(value);
	});
	jQuery.validator.addMethod("passportRecordNumberValidate", function(value, element) {
		return this.optional(element) || /^[0-9]{8}-[0-9]{5}$/i.test(value);
	});
	jQuery.validator.addMethod("dateValidate", function(value, element) {
		return this.optional(element) || value.match(/^(0?[1-9]|[12][0-9]|3[0-1])[/., -](0?[1-9]|1[0-2])[/., -](19|20)?\d{2}$/);
	});
	jQuery.validator.addMethod("creditCard", function(value, element) {
		return this.optional(element) || /^[0-9*]{4} [0-9*]{4} [0-9*]{4} [0-9]{4}$/i.test(value);
	});
	jQuery.validator.addMethod("phoneVerified", function(value, element) {
		return this.optional(element) || !jQuery(element).parent().find('.js-check-phone:not(.hidden):not(.phone-verified)').length;
	}, "Phone not verified");
}

