function initAjaxForm(context) {
	jQuery('.js-ajax-form').each(function(){
		var self = jQuery(this),
			holder = self.closest('.form-holder'),
			buttons = self.find('[data-text-sending]'),
			sending = false;

		self.on('submit', function(e){
			e.preventDefault();

			if (sending || self.valid() !== true) {
				return;
			}

			sending = true;

			holder.find('.flash').remove();

			buttons.each(function(){
				var self = jQuery(this);
				self.attr('data-text-default', self.text());
				self.text(self.attr('data-text-sending'));
			});

			jQuery.ajax({
				url: self.attr('action'),
				type: self.attr('method') ? self.attr('method') : 'GET',
				data: self.serialize(),
				success: function(response, status, xhr) {
					var isJson = (xhr.getResponseHeader('Content-Type') == 'application/json');

					buttons.each(function(){
						var self = jQuery(this);
						self.text(self.attr('data-text-default'));
					});

					if (isJson) {
						if (typeof response["url"] !== "undefined") {
							window.location.replace(response["url"]);
						} else if (typeof response["messages"] !== "undefined") {
							for (var i = 0; i < response["messages"].length; i++) {
								holder.append(jQuery('<div class="flash flash-'+response["messages"][i].type+'">'+response["messages"][i].text+'</div>'));
							}
						}
					} else {
						var newContent = jQuery('<div>'+response+'</div>');
						holder.html(newContent);
						initAjaxForm(newContent);
						initCustomForms();
						initDateInput(newContent);
						initpasswordShow(newContent);
						initAutocomplete(newContent);
						initCreditCardField(newContent);
						initAjaxUploader(newContent);
						validationSteps(newContent);
						initLinkBankCard(newContent);
						initInlineFormEditing(newContent);
						initShowIfValue(newContent);
					}
				},
				error: function(){
					buttons.each(function(){
						var self = jQuery(this);
						self.text(self.attr('data-text-error'));
					});
				},
				complete: function(){
					sending = false;
				}
			});
		});
	});

	
}

