function recalculateBonusesBar()
{
	var formSteps = jQuery('.step-block'),
		activeStep = formSteps.filter('.active'),
		activeStepNumber = formSteps.index(activeStep),
		docUploaded = 0,
		percentComplete = Math.min(activeStepNumber, 5) * 20,
		percentCompleteBars = Math.ceil(percentComplete / 10),
		percentCompleteBarsList = jQuery('.js-indicator-list')
	;
	
	jQuery('.document-uploader-input').each(function(){
		var value = jQuery(this).val();
		if (value && value !== 'null') {
			docUploaded++;
		}
	});
	
	jQuery('.js-bonus-steps li').each(function(i){
		var self = jQuery(this);

		self
			.removeClass('active')
			.removeClass('passed')
		;

		if (i == activeStepNumber) {
			self.addClass('active');
		}
		
		if (i < activeStepNumber) {
			if (i < 4 || ((i == 4) && docUploaded)) {
				self.addClass('passed');
			} else if (i > 4) {
				self.addClass('active');
			}
		}
	});
	
	jQuery('.js-indicator-percent').text(percentComplete + '%');

	percentCompleteBarsList.html('');

	for (var i = 0; i < percentCompleteBars; i++) {
		jQuery('<li class="active"></li>').appendTo(percentCompleteBarsList);
	}

	for (var i = 0; i < 10 - percentCompleteBars; i++) {
		jQuery('<li></li>').appendTo(percentCompleteBarsList);
	}
}

