function initAutocomplete(context) {
	jQuery('.js-address-autocomplete', context).each(function () {
		var self = this,
			autocompleteId = $(this).attr('id'),
			options = {
				types:['geocode']
			},
			autocomplete = new google.maps.places.Autocomplete(self, options);

		var _addEventListener = (self.addEventListener) ? self.addEventListener : self.attachEvent;

		if (self.addEventListener) {
			self.addEventListener = addEventListenerWrapper;
		} else if (self.attachEvent) {
			self.attachEvent = addEventListenerWrapper;
		}

		google.maps.event.addDomListener(self, 'keydown keyup', function(event) {
			if (event.keyCode === 13) {
				event.preventDefault();
				return false;
			}
		});

		autocomplete.addListener('place_changed', fillInAddress);

		function addEventListenerWrapper(type, listener) {
			if (type == "keydown") {
				var orig_listener = listener;
				listener = function (event) {
					var suggestion_selected = $(".pac-item-selected").length > 0;
					if (event.which == 13 && !suggestion_selected) {
						var simulated_downarrow = jQuery.Event("keydown", {keyCode:40, which:40}); orig_listener.apply(self, [simulated_downarrow]);
						event.preventDefault();
					}
					orig_listener.apply(self, [event]);
				};
			}
			_addEventListener.apply(self, [type, listener]);
		}

		function fillInAddress() {
			var place = autocomplete.getPlace();

			jQuery('[data-autocomplete-id='
				+ autocompleteId + ']').val('');

			if(0 < place.address_components.length) {
				for (var i = 0; i < place.address_components.length; i++) {
					var addressType = place.address_components[i].types[0];
					var val = place.address_components[i]['long_name'];
					jQuery('[data-autocomplete-id='
						+ autocompleteId
						+ '][data-autocomplete-field=' + addressType + ']').val(val);
				}
			}
		}
	});
}

